// RecipeTable.js
import React, { useState, useMemo } from 'react'
import { useTable } from 'react-table'
import './RecipeTable.css'

const RecipeTable = () => {
  const [recipes, setRecipes] = useState([])
  const [newRecipe, setNewRecipe] = useState({ name: '', meal: '' })

  const columns = useMemo(
    () => [
      { Header: 'Recipe', accessor: 'name' },
      { Header: 'Meal', accessor: 'meal' },
    ],
    []
  )

  const data = useMemo(() => recipes, [recipes])

  const tableInstance = useTable({ columns, data })

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    tableInstance

  const handleInputChange = (e) => {
    const { name, value } = e.target
    setNewRecipe((prevRecipe) => ({ ...prevRecipe, [name]: value }))
  }

  const handleAddRecipe = () => {
    setRecipes((prevRecipes) => [
      ...prevRecipes,
      { id: prevRecipes.length + 1, ...newRecipe },
    ])
    setNewRecipe({ name: '', meal: '' })
  }

  return (
    <div>
      <h1>Recipe Table</h1>
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row)
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                ))}
              </tr>
            )
          })}
        </tbody>
      </table>
      <div>
        <label>
          Recipe:
          <input
            type="text"
            name="name"
            value={newRecipe.name}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Meal:
          <select
            name="meal"
            value={newRecipe.meal}
            onChange={handleInputChange}
          >
            <option value="">Select Meal</option>
            <option value="breakfast">Breakfast</option>
            <option value="lunch">Lunch</option>
            <option value="dinner">Dinner</option>
          </select>
        </label>
        <button onClick={handleAddRecipe}>Add Recipe</button>
      </div>
    </div>
  )
}

export default RecipeTable
