import './App.css'
import { getImageUrl } from './constants.js'

function Scientist({ person, size }) {
  const s1 = ['Profession: ', 'Awards:  ', 'Discovered: ']
  return (
    <div>
      <h2>{person.name}</h2>
      <img
        className="avatar"
        src={getImageUrl(person)}
        alt={person.name}
        width={size}
        height={size}
      />
      <ul>
        {s1.map((info) => {
          return (
            <li key={info}>
              {' '}
              <b>{info}</b>
              {person.profe}
            </li>
          )
        })}
      </ul>
    </div>
  )
}

export default function Gallery({ person, size }) {
  return (
    <div>
      <h1>Notable Scientists</h1>
      <section className="profile">
        <Scientist
          person={{
            name: 'Maria Skłodowska-Curie',
            imgUrl: 'szV5sdG',
            profe: 'physicist and chemist',
            awards:
              '(Nobel Prize in Physics, Nobel Prize in Chemistry, Davy Medal, Matteucci Medal)',
            disc: 'polonium (chemical element)',
          }}
        />
      </section>
      <section className="profile">
        <Scientist
          person={{
            name: 'Katsuko Saruhashi',
            imgUrl: 'YfeOqp2',
          }}
        />
      </section>
    </div>
  )
}
